import { Component } from 'react'
import './music-visualizer.scss'



class MusicCircle {
    constructor({ size, musicWaveLength, color, context }, setState) {
        this.size = size
        this.musicWaveLength = musicWaveLength
        this.color = color
        this.context = context
        this.setState = setState
        this.context.strokeStyle = this.color
        this.deltaCoef = .8
        this.scaleCoef = this.size / window.innerHeight * this.deltaCoef
        this.radius = (this.size - this.musicWaveLength * 2) / 2
        this.cx = this.cy = this.radius + this.musicWaveLength
        this.tickSize = 10
        this.maxTickSize = this.tickSize * 9 * Math.max(0.5, this.size / window.innerHeight)
        this.countTicks = size / 2
        this.frequencyData = []
        this.PI = 360
        this.loadingAngle = 0
        this.keepRendering = true

        this.render = this.render.bind(this)
        this.render()
    }

    clear () { this.context.clearRect(0, 0, this.size, this.size) }

    draw () {
        this.context.save()
        this.context.beginPath()
        this.context.lineWidth = 1
        const ticks = this.getTicks()
        ticks.forEach(({x1, y1, x2, y2}) => this.drawTick(x1, y1, x2, y2))
        this.context.restore()
    }

    render () {
        this.clear()
        this.draw()
        window.requestAnimationFrame(this.render)
    }

    drawTick (x1, y1, x2, y2) {
        this.context.beginPath()
        this.context.strokeStyle = this.color
        this.context.lineWidth = 2
        this.context.moveTo(this.cx + x1, this.cx + y1)
        this.context.lineTo(this.cx + x2, this.cx + y2)
        this.context.stroke()
    }

    getTicks () {
        var x1, y1, x2, y2, tick, k, sum, coef, delta, deltaIndex, m = [], allScales = []
        const lesser = 170, 
        ticks = this.getTickPoints(), 
        size = this.radius - this.tickSize
        for (var i = 0, len = ticks.length - 1; i < len; i++) {
            coef = 1 - i / (len * 2.5)
            if (i > (ticks.length-1) / 2) coef = 1 - Math.abs((ticks.length-1) - i) / (len * 2.5)

            deltaIndex = i > (ticks.length-1) / 2 ? Math.abs((ticks.length-1) - i) : i
            delta = Math.max(0, ((this.frequencyData[deltaIndex] || 0) - lesser * coef) * this.scaleCoef)

            tick = ticks[i]
            k =  this.radius / (size - delta)

            x1 = tick.x * size
            y1 = tick.y * size
            x2 = x1*k
            y2 = y1*k
            m.push({x1, y1, x2, y2})

            if (i < 5) allScales.push(Math.max(1, delta / 60 / (this.scaleCoef / this.deltaCoef)))
        }

        sum = allScales.reduce((pv, cv) => pv + cv, 0) / allScales.length
        if(this.keepRendering)
            this.setState(prevState => {
                prevState.scale = sum
                return prevState
            });
        return m
    }

    getTickPoints () {
        var rad, coords = []
        const step = this.PI / this.countTicks
        for (var deg = 0; deg < this.PI; deg += step) {
            rad = deg * Math.PI / (this.PI / 2)
            coords.push({ x: Math.cos(rad), y: -Math.sin(rad), angle: deg })
        }
        return coords
    }
}

class MusicVisualizer extends Component {
	constructor(props) {
        super(props)

		this.state = {
            currentTime: '0:00',
            scale: 1
        }

        this.playerDetails = {
            size: 560,
            musicWaveLength: 78,
            color: '#FFFFFF'
        }

        this.musicCircleInstance = null
        this.timerIntervalInstance = null
        this.javascriptNode = null

        this.playAudio = this.playAudio.bind(this)
        this.pauseAudio = this.pauseAudio.bind(this)
        this.nextTrack = this.nextTrack.bind(this)
        this.prevTrack = this.prevTrack.bind(this)
        this.toggleSound = this.toggleSound.bind(this)
        this.keepTime = this.keepTime.bind(this)
    }

    playAudio() { this.refs.audio.play() }

    pauseAudio() { this.refs.audio.pause() }

    nextTrack() {
        var i = this.props.currentTrackIndex
        do {i = i+1 >= this.props.tracks.length ? 0 : ++i} while(!this.props.tracks[i].url)
        this.props.onPlay(null, i)
    }

    prevTrack() {
        var i = this.props.currentTrackIndex
        do {i = i-1 < 0 ? this.props.tracks.length - 1 : --i} while(!this.props.tracks[i].url)
        this.props.onPlay(null, i)
    }

    toggleSound(e) { e.target.classList.toggle('disabled') ? this.refs.audio.muted = true : this.refs.audio.muted = false }

    keepTime() {
        const rawTime = parseInt(this.refs.audio.currentTime || 0),
        secondsInMin = 60,
        min = parseInt(rawTime / secondsInMin)
        var seconds = rawTime - min * secondsInMin

        if (seconds < 10) seconds = '0' + seconds

        this.setState({ currentTime: min + ':' + seconds })
    }

    getBackSide() {
        return (
            <div style={{ position: 'relative' }}>

                <div className="album-background" style={{ backgroundImage: `url('${this.props.tracks[this.props.currentTrackIndex].cover}')` }}>
                    <div className="album-overlay"/>
                </div>

                <div ref="player" className="player" >

                    <div className="canvas-wrapper">
                        <div className="inner-canvas-wrapper" style={{ transform: `scale(${this.state.scale})`}}>
                            <canvas className="visual" width={this.playerDetails.size} height={this.playerDetails.size} ref="visual" />
                        </div>
                    </div>

                    <audio ref="audio" crossOrigin="anonymous" src={ this.props.tracks[this.props.currentTrackIndex].url } onEnded={ this.props.onPause }/>

                    <div className="song">
                        <div className="artist" >{ this.props.tracks[this.props.currentTrackIndex].artist }</div>
                        <div className="name" >{ this.props.tracks[this.props.currentTrackIndex].song }</div>
                    </div>

                    <div className="play-area">
                        <div className="prev-song" onClick={ this.prevTrack }/>
                        <div className="play" style={ this.props.playing ? { display: 'none' } : { display: 'inline-block' } } onClick={ this.props.onPlay }/>
                        <div className="pause" style={ this.props.playing ? { display: 'inline-block' } : { display: 'none' } } onClick={ this.props.onPause }/>
                        <div className="next-song" onClick={ this.nextTrack }/>
                        <div className="sound-control" onClick={ this.toggleSound }/>
                    </div>

                    <div className="time">{ this.state.currentTime }</div>

                </div>

            </div>
        )
    }

	componentDidMount() {
        var self = this;
        this.musicCircleInstance = new MusicCircle({ ...self.playerDetails, context: self.refs.visual.getContext('2d') }, this.setState.bind(this))
        window.AudioContext = window.AudioContext || window.webkitAudioContext

        const context = new AudioContext()
        this.javascriptNode = context.createScriptProcessor(2048, 1, 1)
        const destination = context.destination,
        player = context.createAnalyser(),
        source = context.createMediaElementSource(this.refs.audio),
        gainNode = context.createGain()

        this.javascriptNode.connect(destination)
        player.connect(this.javascriptNode)
        player.smoothingTimeConstant = 0.7
        player.fftSize = 2048
        source.connect(gainNode)
        gainNode.connect(player)
        gainNode.connect(destination)

        this.javascriptNode.onaudioprocess = () => {
            self.musicCircleInstance.frequencyData = new Uint8Array(player.frequencyBinCount)
            player.getByteFrequencyData(self.musicCircleInstance.frequencyData)
        }

        this.timerIntervalInstance = setInterval(this.keepTime, 300)
    }

    // componentWillReceiveProps() { }

    componentDidUpdate() { this.props.playing ? this.playAudio() : this.pauseAudio() }

    componentWillUnmount() { 
        clearInterval(this.timerIntervalInstance)
        this.musicCircleInstance.keepRendering = false
    }

	render() {
		return (
            <div className={`flip-container ${this.props.showingDial ? 'flip' : ''}`} style={ this.props.style } >
                <div className="flipper" style={{ width: `${this.playerDetails.size}px`, height: `${this.playerDetails.size}px` }}>
                    <div className="front" style={{ backgroundImage: `url('${this.props.tracks[this.props.currentTrackIndex].cover}')` }}/>
                    <div className="back">{this.getBackSide()}</div>
                </div>
            </div>
		)
	}
}

export default MusicVisualizer
