.player {
    display: flex;
    align-items: center;
    position: relative;
    z-index:10;
    user-select: none;
    font-weight:100;
    overflow: hidden;

    .canvas-wrapper {
        transform:rotateZ(-90deg);
        margin: 0 auto;

        .visual {
            will-change:transform, width, height;
            transform:translateZ(0);
            border-radius:50%;
        }
    }

    .play-area, >.song, >.time {
        position: absolute;
        left: 50%;
        transform: translate(-50%, 0);
    }

    .play-area {
        div {
            display: inline-block;
        }

        .prev-song:hover,
        .next-song:hover,
        .pause:hover,
        .play:hover {
            opacity: 0.7;
        }

        .play, .pause {
            cursor: pointer;
            opacity: 0.85;
            vertical-align: middle;
            margin: 0 26px;
            border: 3px solid #ffffff;
            border-radius: 50px;
            width: 75px;
            height: 75px;
        }

        .play {
            background: url(data:image/svg+xml;base64,) no-repeat;
            background-size: 50px 50px;
            background-position-x: 17px;
            background-position-y: 13px;
        }

        .pause {
            background: url(data:image/svg+xml;base64,) no-repeat;
            background-size: 50px 50px;
            background-position-x: 13px;
            background-position-y: 13px;
        }

        .prev-song, .next-song {
            cursor: pointer;
            vertical-align: middle;
            width: 41px;
            height: 27px;
        }

        .prev-song {
            background: url(data:image/svg+xml;base64,) 0 0 no-repeat;
            transform:rotateY(180deg);
        }

        .next-song {
            background: url(data:image/svg+xml;base64,) 0 0 no-repeat;
        }

        .sound-control {
            cursor: pointer;
            width: 31px;
            height: 27px;
            position: absolute;
            top: 120%;
            left: 50%;
            margin-left: -16px;
            background-image: url(data:image/svg+xml;base64,);
            background-size: 31px 27px;
            text-align: center;
        }

        .sound-control.disable {
            opacity: 0.4;
        }

        .sound-control:hover {
            opacity: 0.7;
        } 
    }

    .song {
        font-family: Roboto, sans-serif;
        color:  #ffffff;
        position: absolute;
        top: 28%;
        text-align: center;

        .artist {
            font-size: 22px;
            margin-bottom: 5px;
        }

        .name {
            font-size: 18px;
        }
    }

    .time {
        text-align: center;
        font-family: Roboto, sans-serif;
        color:  #ffffff;
        position: absolute;
        left: 50%;
        font-size: 20px;
        bottom: 22%;
        font-weight:100;
    }
}
