.flip-container {
    perspective: 1000px;

    >.flipper {
        transition: 0.6s;
        transform-style: preserve-3d;
        height: 100%;
        position: relative;
        margin: 0 auto;

        >.front, >.back {
            height: 100%;
            width: 100%;
            backface-visibility: hidden;
            border-radius: 45px;
            box-shadow: 0 10px 16px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
            position: absolute;
            top: 0;
            left: 0;
        }

        >.front {
            z-index: 2;
            background-color: #191414;
            background-size: contain;
            background-repeat: no-repeat;
            background-position-y: 45%;
            transform: rotateY(0deg);
        }

        >.back, >.back .album-background, >.back .album-background .album-overlay {
            background-color: var(--blue-primary);
            border-radius: 50px;
            opacity: .85;
            width: 100%;
            height: 100%;
        }

        >.back {
            transform: rotateY(180deg);

            .album-background {
                background-size: cover;
                background-position: center;
                background-repeat: no-repeat; 
                position: absolute;
                filter: blur(3px);
            }

            .player {
                display: flex;
                align-items: center;
                position: relative;
                z-index:10;
                user-select: none;
                font-weight:100;
                overflow: hidden;

                >.canvas-wrapper {
                    transform:rotateZ(-90deg);
                    margin: 0 auto;

                    .visual {
                        will-change:transform, width, height;
                        transform:translateZ(0);
                        border-radius:50%;
                    }
                }

                >.play-area, >.song, >.time {
                    position: absolute;
                    left: 50%;
                    transform: translate(-50%, 0);
                }

                >.play-area {
                    >div {
                        display: inline-block;
                    }

                    >.prev-song:hover,
                    >.next-song:hover,
                    >.pause:hover,
                    >.play:hover {
                        opacity: 0.7;
                    }

                    >.play, >.pause {
                        cursor: pointer;
                        opacity: 0.85;
                        vertical-align: middle;
                        margin: 0 26px;
                        border: 3px solid #ffffff;
                        border-radius: 50px;
                        width: 75px;
                        height: 75px;
                    }

                    >.play {
                        background: url(data:image/svg+xml;base64,) no-repeat;
                        background-size: 50px 50px;
                        background-position-x: 17px;
                        background-position-y: 13px;
                    }

                    >.pause {
                        background: url(data:image/svg+xml;base64,) no-repeat;
                        background-size: 50px 50px;
                        background-position-x: 13px;
                        background-position-y: 13px;
                    }

                    >.prev-song, >.next-song {
                        cursor: pointer;
                        vertical-align: middle;
                        width: 41px;
                        height: 27px;
                    }

                    >.prev-song {
                        background: url(data:image/svg+xml;base64,) 0 0 no-repeat;
                        transform:rotateY(180deg);
                    }

                    >.next-song {
                        background: url(data:image/svg+xml;base64,) 0 0 no-repeat;
                    }

                    >.sound-control {
                        cursor: pointer;
                        width: 31px;
                        height: 27px;
                        position: absolute;
                        top: 120%;
                        left: 50%;
                        margin-left: -16px;
                        background-image: url(data:image/svg+xml;base64,);
                        background-size: 31px 27px;
                        text-align: center;
                    }

                    >.sound-control.disable {
                        opacity: 0.4;
                    }

                    >.sound-control:hover {
                        opacity: 0.7;
                    } 
                }

                >.song {
                    font-family: Roboto, sans-serif;
                    color:  #ffffff;
                    position: absolute;
                    top: 28%;
                    text-align: center;

                    >.artist {
                        font-size: 22px;
                        margin-bottom: 5px;
                    }

                    >.name {
                        font-size: 18px;
                    }
                }

                >.time {
                    text-align: center;
                    font-family: Roboto, sans-serif;
                    color:  #ffffff;
                    position: absolute;
                    left: 50%;
                    font-size: 20px;
                    bottom: 22%;
                    font-weight:100;
                }
            }
        }
    }
}

.flip-container.flip .flipper {
  transform: rotateY(180deg);
}
